/* tslint:disable */
/* eslint-disable */
/**
* @param {IConfig} config
* @returns {IResult}
*/
export function parse(config: IConfig): IResult;

export function parseAsync(config: IConfig): Promise<IResult>;


export interface ExportSpecifier {
    /**
     * Export name 
     */
    n: string;
    /**
     * Export origin name 
     */
    ln: string | null;
    /**
     * Export name start index 
     */
    s: number;
    /**
     * Export name end index 
     */
    e: number;
    /**
     * Export origin name start index 
     */
    ls: number;
    /**
     * Export origin name end index 
     */
    le: number;
}

export interface ImportSpecifier {
    /**
     * Source name 
     */
    n: string | null;
    /**
     * Source start index 
     */
    s: number;
    /**
     * Source end index 
     */
    e: number;
    /**
     * Import start index 
     */
    ss: number;
    /**
     * Import end index 
     */
    se: number;
    /**
     * Dynamic import start index 
     */
    d: number;
    /**
     * Assert object start index (include `{}`) 
     */
    a: number;
    /**
     * Type of import statement 
     */
    t: 1 | 2 | 3 | 4 | 5;
}

export interface ParseResult {
    filename: string;
    imports: ImportSpecifier[];
    exports: ExportSpecifier[];
    facade: boolean;
    hasModuleSyntax: boolean;
}

export interface ParseOptions {
    filename: string;
    code: string;
}

export interface IResult {
    output: ParseResult[];
}

export interface IConfig {
    input: ParseOptions[];
}



